using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using Photon.Pun;

public class DartPlayerAnimation : MonoBehaviourPun
{
    [SerializeField] private DartPlayerMovement playerMovement = null;
    [SerializeField] private PlayerController playerController = null;

    [SerializeField] private Animator _Animator = null;

    private void Update()
    {
        if (photonView.IsMine == false) return;
        _Animator.SetBool("walk", playerMovement.IsMove);
        _Animator.SetBool("hold", playerController.IsHold);

        if (Input.GetMouseButtonUp(0))
        {
            _Animator.SetTrigger("throw");
        }
    }
}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    